void animation_phase(void) {
	if (get_current_wpm() <= IDLE_SPEED){
		current_idle_frame = (current_idle_frame + 1) % IDLE_FRAMES;
		oled_write_raw_P(idle[abs((IDLE_FRAMES-1)-current_idle_frame)], ANIM_SIZE);
	 }
	 if (get_current_wpm() > IDLE_SPEED && get_current_wpm() < TAP_SPEED){
		 oled_write_raw_P(prep[0], ANIM_SIZE);
	 }
	 if (get_current_wpm() >= TAP_SPEED){
		 current_tap_frame = (current_tap_frame + 1) % TAP_FRAMES;
		 oled_write_raw_P(tap[abs((TAP_FRAMES-1)-current_tap_frame)], ANIM_SIZE);
	 }
}

void render_animation(void) {
	if (get_current_wpm() != 0) {
		oled_on();

		if (timer_elapsed32(anim_timer) > ANIM_FRAME_DURATION) {
			anim_timer = timer_read32();
			animation_phase();
		}

		anim_sleep = timer_read32();
	} else {
		if (timer_elapsed32(anim_sleep) > OLED_TIMEOUT) {
			oled_off();
		} else {
			if (timer_elapsed32(anim_timer) > ANIM_FRAME_DURATION) {
				anim_timer = timer_read32();
				animation_phase();
			}
		}
	}
}

char wpm_str[8];

void write_wpm_str(void) {
	oled_set_cursor(13, 5);
	sprintf(wpm_str, "WPM: %03d", get_current_wpm());
	oled_write(wpm_str, false);
}

char layer_str[13];

void write_layer_str(void) {
	switch (get_highest_layer(layer_state | default_layer_state)) {
		case _QWERTY:
			sprintf(layer_str, "Layer: QWERTY");
			break;
		case _NAV:
			sprintf(layer_str, "Layer:    NAV");
			break;
		case _SYM:
			sprintf(layer_str, "Layer: SYMBOL");
			break;
		case _FUNCTION:
			sprintf(layer_str, "Layer:   FUNC");
			break;
		case _ADJUST:
			sprintf(layer_str, "Layer: ADJUST");
			break;
		case _MOUSE:
			sprintf(layer_str, "Layer:  MOUSE");
			break;
		default:
			sprintf(layer_str, "Layer: ??????");
	}

	oled_set_cursor(8, 6);
	oled_write(layer_str, false);
}

